mapper.insert:
INSERT INTO h_member (
      mid
    , mpw
    , mname
    , mphone
    ,mdate
    ,mproyn
    ,ano
) VALUES (
      #{mid}
    , #{mpw}
    , #{mname}
    , #{mphone}
    ,sysdate
    ,#{mproyn}
    ,#{ano}
)

mapper.update:
UPDATE H_MEMBER SET 
 mpw=#{mpw},
  mname=#{mname},
   mphone=#{mphone}
   WHERE mid=#{mid}
